INSERT INTO `wp_postmeta` VALUES (317,16,'descricao','Quando iniciamos a Bikcraft, queríamos apenas um produto que adoraríamos utilizar. Éramos apaixonados por pedalar e também por fazer as coisas com as nossas próprias mãos. Assim surgiu um sonho na garagem da nossa casa.\r\n\r\nConheça os nossos produtos, pergunte para os nossos clientes e descubra a maravilha de ter uma Bikcraft na sua casa.');
INSERT INTO `wp_postmeta` VALUES (318,16,'_descricao','field_60c36850f78cb');
